import React, { Component } from "react";
import { Row, Col, Layout, Select } from "antd";
import { observable } from "mobx";
import { observer } from "mobx-react";
import { SimpleCard } from "../DefaultComponents/Card"
import { SimpleInput } from "../DefaultComponents/Input"
import { Domain, periodUnit, verifiedProvider } from '../../models/Domain'
import { DownOutlined } from '@ant-design/icons'
import { verifiedProvidersList } from '../../Constants/verifiedProviders'
const { Content } = Layout;
const { Option } = Select;

type domainOperation = 'registration' | 'renewal' | 'deletion' | 'info'

class RegistryVM {
  @observable registeredDomains: Domain[] = []
  @observable domain: Domain = new Domain()
  @observable operation: 

  handlePeriodChange = (value: periodUnit) => {
    this.domain.period.unit = value
  }

  handleProviderChange = (value: verifiedProvider) => {
    this.domain.contact.provider = value
  }
}

@observer
export default class Registry extends Component {
  vm = new RegistryVM();

  render() {
    return (
      <Layout>
        <Content>
          <Row justify='center' align="middle" style={{minHeight: '100vh'}}>
            <Col xs={23}>
              <Row justify='center' align="middle">
                <Col xs={23} sm={16}>
                  <SimpleCard>
                    <Row justify="center">
                      <Col span={24}>
                        <h2>Domain Name Registration</h2> 
                      </Col>
                    </Row>
                    {}
                    <Row justify="center">
                      <Col span={24}>
                        <SimpleInput title="Domain Name" placeholder="Domain Name" changed={(event) => {this.vm.domain.name = event.target.value}}/>
                      </Col>
                    </Row>
                    <Row>
                      <Col span={12}>
                        <SimpleInput title="Period" placeholder="Number of Years" changed={(event) => {this.vm.domain.period.value = Number(event.target.value)}}/>
                      </Col>
                      <Col span={12}>
                        <Row justify='end'>
                          <Select defaultValue="year" style={{ paddingTop: '30px', width: '90%', borderRadius: '5px'}} showArrow={false} onChange={this.vm.handlePeriodChange}>
                            <Option value="year">year</Option>
                          </Select>
                        </Row>
                      </Col>
                    </Row>
                    <Row justify="center">
                      <Col span={24}>
                        <h4>Verfied Contact Provider</h4>
                        <Select defaultValue="provider-abc" style={{width: '100%', marginBottom: '15px'}} showArrow={false} onChange={this.vm.handleProviderChange}>
                          {verifiedProvidersList.map(provider => {
                            return (
                              <Option value={provider}>{provider}</Option>
                            )
                          })}
                        </Select>
                      </Col>
                    </Row>
                    <Row justify="center">
                      <Col span={24}>
                        <SimpleInput title="Verified Contact ID" placeholder="Verified Contact ID" changed={(event) => {this.vm.domain.contact.id = event.target.value}}/>
                      </Col>
                    </Row>
                  </SimpleCard>
                </Col>
              </Row>
            </Col>
          </Row>
        </Content>
      </Layout>
    );
  }
}
